mapper.select:
select no, title, writer, content, regtime
  from notice 
 where no = #{no}

mapper.insert:
insert into notice(
	title, writer, content
) values (
	#{title}, #{writer}, #{content}
)

mapper.update:
update notice 
   set title = #{title}, 
   	   writer = #{writer}, 
   	   content = #{content}
 where no = #{no}
